              parrafo = "<p>Estimado "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaComercial.Nombre)+" "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaComercial.Paterno)+" le informamos que el servicio "+validaCampoLLeno(osAtendido[0].OrdenServicio.Servicio.Nombre)+" con folio "+validaCampoLLeno(osAtendido[0].OrdenServicio.Folio)+", <b>ha sido finalizado y validado</b> por "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaValida.Nombre)+" "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaValida.Paterno)+":</p>"+getUltimoEvento(osAtendido)+"<p>Observaciones:</p><p>finalizaDesc</p><p>Adjunto a este correo se encontrará la evidencia generada.</p>";
              return parrafo;
            case "factura":
              parrafo = "<p>Estimado "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaComercial.Nombre)+" "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaComercial.Paterno)+" le informamos que el servicio "+validaCampoLLeno(osAtendido[0].OrdenServicio.Servicio.Nombre)+", <b>ha sido facturado</b> por "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaValida.Nombre)+" "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaValida.Paterno)+":</p>"+getUltimoEvento(osAtendido)+"<p>Adjunto a este correo se encontrará la evidencia generada.</p>";
              return parrafo;
            case "cobrado":
              parrafo = "<p>Estimado "+validaCampoLLeno(os.PersonaComercial.Nombre)+" "+validaCampoLLeno(os.PersonaComercial.Paterno)+" le informamos que el servicio "+validaCampoLLeno(os.Servicio.Nombre)+" con folio "+validaCampoLLeno(os.Folio)+", <b>ha sido COBRADO</b></p>";
              return parrafo;
            case "nocobrado":
              parrafo = "<p>Estimado "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaComercial.Nombre)+" "+validaCampoLLeno(osAtendido[0].OrdenServicio.PersonaComercial.Paterno)+" le informamos que el servicio "+validaCampoLLeno(osAtendido[0].OrdenServicio.Servicio.Nombre)+" en "+validaCampoLLeno(osAtendido[0].OrdenServicio.Ubicacion.Nombre)+" con folio "+validaCampoLLeno(osAtendido[0].OrdenServicio.Folio)+", <b>NO HA SIDO COBRADO</b>.</p>";
              return parrafo;
          }
          return parrafo;
        case "todos":
          switch(que){
            case "cobrado":
              parrafo = String.Format("<p>Se les comunica que el servicio {0} con folio {1} del cliente {2} con ubicación {3} ha sido <b>COBRADO</b></p>", os.Servicio.Nombre, os.Folio, os.Cliente.RazonSocial, os.Ubicacion.Nombre);
              return parrafo;
            case "nocobrado":
              parrafo = String.Format("<p>Se les comunica que el servicio {0} con folio {1} del cliente {2} con ubicación {3} <b>NO</b> ha sido <b>COBRADO</b></p>", os.Servicio.Nombre, os.Folio, os.Cliente.RazonSocial, os.Ubicacion.Nombre);
              return parrafo;
            case "cancelado":
              parrafo = String.Format("<p>Se les comunica que el servicio {0} con folio {1} del cliente {2} con ubicación {3} ha sido <b>CANCELADO</b></p>", os.Servicio.Nombre, os.Folio, os.Cliente.RazonSocial, os.Ubicacion.Nombre);
              return parrafo;
          }
          return parrafo;
      }
      return parrafo;
    }

    private static string formateaEstatusNuevo(string e){
      switch(e){
        case "sitio":
          return "En sitio";
        case "noatendido":
          return "Reporte actividad";
        case "atendido":
          return "Atendido";
      }
      return "Estatus indefinido";
    }

    private static string getOSAtendidoInfo(List<Operador> osAtendido){
      if(osAtendido != null && osAtendido.Count > 0){
        StringBuilder info = new StringBuilder();
        foreach(Operador atendido in osAtendido){
          foreach(Operador.Estado estado in atendido.EstadoN){
            info.Append(String.Format("<p align=\"left\">{0}</p><p align=\"left\">{1}</p><p align=\"left\">{2}</p><p align=\"left\">{3}</p><p align=\"left\">{4}</p><p align=\"left\">{5}</p>",atendido.OrdenServicio.Folio, atendido.OrdenServicio.FechaInicio, atendido.OrdenServicio.FechaFin, atendido.OrdenServicio.Cliente.RazonSocial, atendido.OrdenServicio.Ubicacion.Nombre, atendido.OrdenServicio.Ubicacion.Direccion));
          }
        }
        return info.ToString();
      }
      return "No existe instancia OSAtendido";
    }

    private static string getUltimoEvento(List<Operador> osAtendido){
      if(osAtendido != null && osAtendido.Count > 0){
        int ultimoOperador = osAtendido.Count-1;
        int ultimoOperadorEstadoN = osAtendido[ultimoOperador].EstadoN.Count-1;
        string ultimoEvento = String.Format("<p align=\"left\">{0} - <b>{1}</b><p>{2}</p>", validaCampoLLeno(osAtendido[ultimoOperador].Hora.ToString("f", CultureInfo.CreateSpecificCulture("es-ES"))), validaCampoLLeno(formateaEstatusNuevo(osAtendido[ultimoOperador].EstadoN[ultimoOperadorEstadoN].NuevoEstado)), validaCampoLLeno(osAtendido[ultimoOperador].EstadoN[ultimoOperadorEstadoN].ComentarioNuevoEstado));
        return ultimoEvento;
      }
      return "No existe actividad reportada";
    }

    private static string getEventosDelOS(List<Operador> osAtendido){
      StringBuilder _eventos = new StringBuilder();
      if(osAtendido != null){
        foreach(Operador atencion in osAtendido){
          foreach(Operador.Estado estado in atencion.EstadoN){
            _eventos.Append(String.Format("<p align=\"left\">{0} - <b>{1}</b><p>{2}</p>", validaCampoLLeno(atencion.Hora.ToString("f", CultureInfo.CreateSpecificCulture("es-ES"))), validaCampoLLeno(formateaEstatusNuevo(estado.NuevoEstado)), validaCampoLLeno(estado.ComentarioNuevoEstado)));
          }
        }
        return _eventos.ToString();
      } else {
        return "<p align=\"left\"><b>Sin reporte de actividades generados</b></p>";
      }
      return "<p align=\"left\"><b>Sin reporte de actividades generados</b></p>";
    }

    private static string getDatosOS(OrdenServicio os){
      string eventos = new string("");
        eventos = String.Format("<p align=\"left\">{0} {1} {2}</p>", validaCampoLLeno(os.Folio),validaCampoLLeno(os.Cliente.RazonSocial),validaCampoLLeno(os.Ubicacion.Nombre));
      return eventos;
    }

